HallarCateto[n_] := Module[{cat = RandomChoice[Range[1, 10 n]], hip = RandomChoice[Range[11 n, 20 n]], ValoresUsados1 = {}, ValoresUsados2 = {}, ValoresNoUsados1 = Range[1, 10 n], ValoresNoUsados2 = Range[11 n, 20 n], i, Items = {}}, ValoresUsados1 = Union[ValoresUsados1, {cat}]; ValoresUsados2 = Union[ValoresUsados2, {hip}]; ValoresNoUsados1 = Complement[ValoresNoUsados1, ValoresUsados1]; ValoresNoUsados2 = Complement[ValoresNoUsados2, ValoresUsados2]; For[i = 1, i <= n, Items = Append[Items, Row[{StringTemplate[“En un triángulo rectángulo se sabe que la longitud de la hipotenusa es ` ` y que la longitud de uno de sus catetos es ` `. Determine la longitud del otro cateto: ”][ToLatex[Evaluate@hip], ToLatex[Evaluate@cat]], ShortNumerical[{Evaluate@N[
hip ^ 2 - cat ^ 2
]}, precision ->0.2]}]]; cat = RandomChoice[ValoresNoUsados1]; hip = RandomChoice[ValoresNoUsados2]; ValoresUsados1 = Union[ValoresUsados1, {cat}]; ValoresUsados2 = Union[ValoresUsados2, {hip}]; ValoresNoUsados1 = Complement[ValoresNoUsados1, ValoresUsados1]; ValoresNoUsados2 = Complement[ValoresNoUsados2, ValoresUsados2]; i++]; Items] HallarCateto[5]
